import React, { useState } from "react";
import ExpenseForm from './ExpenseForm';
import './NewExpense.css'

const NewExpense = (props) => {

  const [formButton, setFormButton] = useState("unclicked")

  const addExpenseHandler = (event) => {
    setFormButton("clicked")
  }

  const cancelExpenseHandler = (event) => {
    setFormButton("unclicked")
  }

  const saveExpenseDataHandler = (enteredExpenseData) => {
    const expenseData = {
      // Means to unpack the values inside
      ...enteredExpenseData,
      id: Math.random().toString()
    };
    // Using props to pass values up to parent
    // (Pass data so we're lifting the state up)
    props.onAddExpense(expenseData)
    setFormButton("unclicked")
  }

  if (formButton === "clicked") {
    return (
      <div className='new-expense'>
        {/* Use on because we're going to have a function for the prop to define 
        behaviour when something happens */}
        <ExpenseForm clicked={formButton} onSaveExpenseData={saveExpenseDataHandler}/>
      </div>
    );
  }

  return (
    <div className="new-expense">
      <button onClick={addExpenseHandler}>Add New Expense</button>
    </div>

  )



};

export default NewExpense;
