import React, { useState } from "react";
import Expenses from "./components/Expenses/Expenses";
import NewExpense from "./components/NewExpense/NewExpense";

const initialExpenses = [
  { id: "e2",
    title: "New TV",
    amount: 799.49, 
    date: new Date(2021, 2, 12)},
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];

// Arrow functions can replace the word function
const App = () => {
  const [expenses, setExpenses] = useState(initialExpenses);

  const addExpenseHandler = (expense) => {
    // Add item to front and populate rest of array with existing
    // Below is way of updating state when based on an older state
    setExpenses((prevExpenses) => {
      return [expense, ...prevExpenses];
    });
  };
  return (
    <div>
      <NewExpense onAddExpense={addExpenseHandler} />
      {console.log(expenses)}
      <Expenses items={expenses} />
    </div>
  );
  // Can pass an object instead of individual as props using
  // expenses[0] then useprops.expenses.attribute
};

export default App;
