import React, { useState } from "react";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter";
import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";

// Using two expense items
export default function Expenses(props) {
  // set function goes in the higher level one
  const [selectedYear, setSelectedYear] = useState("All");
  // This is what we pass down
  const filterExpenseHandler = (filterYear) => {
    setSelectedYear(filterYear);
  };

  // Need to use two-way binding to use the default value
  return (
    <div>
    <Card className="expenses">
  {/* Using two-way binding creates a controlled component*/}
    <ExpensesFilter selected={selectedYear} onChangeFilter={filterExpenseHandler}/>
    {/* Map creates a new array from an initial array, making some kind
    of transformation to the elements in that array */}
    {/* Below maps an expense from the expense array into an expense item  
    JSX element*/}
    {consle.log(props.items)}
    {props.items.map(expense => <ExpenseItem title={expense.title} 
    amount={expense.amount} date={expense.date} />)}
    </Card>
    </div>
  );
}
